package project;

import java.util.Scanner;

public class Main {

	public static int tabs = 0;

	// Ide soroljátok fel a többi menüpontot
	static void printMenu() {
		System.out.print("6. Váltó állítása\n");
		System.out.print("? Please pick an option: ");
	}

	public static void main(String[] args) {

		Scanner sc = new Scanner(System.in);
		int option;

		printMenu();
		option = sc.nextInt();

		// Az elsőhöz hasonlóan a többi case-t
		switch(option) {
			case 6: gameEnding(); tabs++;
				break;

			default: System.out.print("\1nPick a viable option: ");
		}

	}

	private static void gameEnding(){
		System.out.println("- 6. Játék kimenete");

		Model m = new Model();
		m.moveEngines();
	}

}
